import React, { useState, useEffect } from "react";
import "./NewsCardList.css";
import NewsCard from "../NewsCard/NewsCard";
import SaveIcon from "../../images/SavedIcon.svg"; // Adjust the path as necessary

function NewsCardList({ news, isLoggedIn }) {
  const [visibleCards, setVisibleCards] = useState(3);
  const [savedArticles, setSavedArticles] = useState([]);

  useEffect(() => {
    if (isLoggedIn) {
      // Fetch saved articles from your API
      // This is a placeholder, replace with your actual API call
      fetchSavedArticles().then((articles) => setSavedArticles(articles));
    }
  }, [isLoggedIn]);

  const showMore = () => {
    setVisibleCards((prevVisibleCards) => prevVisibleCards + 3);
  };

  const handleSaveClick = () => {
    if (!isLoggedIn) {
      alert("Sign in to save articles");
    }
  };

  const handleSaveArticle = (article) => {
    // Call your API to save the article
    // This is a placeholder, replace with your actual API call
    saveArticleToAPI(article).then(() => {
      setSavedArticles([...savedArticles, article]);
    });
  };

  const handleRemoveArticle = (article) => {
    // Call your API to remove the article
    // This is a placeholder, replace with your actual API call
    removeArticleFromAPI(article).then(() => {
      setSavedArticles(
        savedArticles.filter((savedArticle) => savedArticle._id !== article._id)
      );
    });
  };

  return (
    <section className="news-card-list">
      <h2 className="news-card-list__title">Search results</h2>
      <div className="news-card-list__cards">
        {news && news.length > 0 ? (
          news
            .slice(0, visibleCards)
            .map((article, index) => (
              <NewsCard
                key={article._id}
                article={article}
                isLoggedIn={isLoggedIn}
                isSaved={savedArticles.some(
                  (savedArticle) => savedArticle._id === article._id
                )}
                onSaveArticle={handleSaveArticle}
                onRemoveArticle={handleRemoveArticle}
              />
            ))
        ) : (
          <p>No articles found. Try a different search term.</p>
        )}
      </div>
      {news && visibleCards < news.length && (
        <button className="news-card-list__button" onClick={showMore}>
          Show more
        </button>
      )}
    </section>
  );
}

export default NewsCardList;
