import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { getNews } from "../../utils/api";
import { auth } from "../../utils/firebase";
import { onAuthStateChanged } from "firebase/auth";
import { register, login, logout } from "../../utils/auth";
import "../../globals.css";
import "./App.css";
import Header from "../Header/Header";
import Main from "../Main/Main";
import SavedNews from "../SavedNews/SavedNews";
import Footer from "../Footer/Footer";
import SignUpModal from "../SignUpModal/SignUpModal";
import SignInModal from "../SignInModal/SignInModal";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [userName, setUserName] = useState("");
  const [news, setNews] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [searchError, setSearchError] = useState(null);
  const [isSignUpModalOpen, setIsSignUpModalOpen] = useState(false);
  const [isSignInModalOpen, setIsSignInModalOpen] = useState(false);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsLoggedIn(true);
        setCurrentUser(user);
        setUserName(user.displayName || user.email);
      } else {
        setIsLoggedIn(false);
        setCurrentUser(null);
        setUserName("");
      }
    });

    return () => unsubscribe();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setSearchError(null);
    try {
      const articles = await getNews(searchQuery);
      setNews(articles);
    } catch (error) {
      console.error("Error searching news:", error);
      setSearchError("Failed to fetch news. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignUp = async (email, password, username) => {
    console.log("handleSignUp called with:", email, password, username);
    try {
      const user = await register(email, password, username);
      console.log("Registration successful:", user);
      setIsSignUpModalOpen(false);
      // Additional logic after successful registration
    } catch (error) {
      console.error("Registration error:", error.message);
      // Handle error (e.g., show error message to user)
    }
  };

  const handleSignIn = async (email, password) => {
    try {
      await login(email, password);
      setIsSignInModalOpen(false);
      // The auth state listener will update isLoggedIn and currentUser
    } catch (error) {
      console.error("Login error:", error);
      // Handle login error (e.g., show error message)
    }
  };

  const handleSignOut = async () => {
    try {
      await logout();
      // The auth state listener will update isLoggedIn and currentUser
    } catch (error) {
      console.error("Logout error:", error);
    }
  };

  return (
    <Router>
      <div className="app">
        <Header
          isLoggedIn={isLoggedIn}
          onSignOut={handleSignOut}
          userName={userName}
          onSignIn={() => setIsSignInModalOpen(true)}
          onSignUp={() => setIsSignUpModalOpen(true)}
        />
        <Routes>
          <Route
            path="/"
            element={
              <Main
                onSearch={handleSearch}
                searchQuery={searchQuery}
                setSearchQuery={setSearchQuery}
                news={news}
                isLoading={isLoading}
                searchError={searchError}
              />
            }
          />
          <Route path="/saved-news" element={<SavedNews />} />
        </Routes>
        <Footer />
        <SignUpModal
          isOpen={isSignUpModalOpen}
          onClose={() => setIsSignUpModalOpen(false)}
          onSignUp={handleSignUp}
          openSignIn={() => {
            setIsSignUpModalOpen(false);
            setIsSignInModalOpen(true);
          }}
        />
        <SignInModal
          isOpen={isSignInModalOpen}
          onClose={() => setIsSignInModalOpen(false)}
          onSignIn={handleSignIn}
          openSignUp={() => {
            setIsSignInModalOpen(false);
            setIsSignUpModalOpen(true);
          }}
        />
      </div>
    </Router>
  );
}

export default App;
