import { auth } from "./firebase";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut,
  updateProfile,
} from "firebase/auth";

export const register = async (email, password, username) => {
  try {
    const userCredential = await createUserWithEmailAndPassword(
      auth,
      email,
      password
    );
    const user = userCredential.user;

    // Update the user's profile with the username
    await updateProfile(user, {
      displayName: username,
    });

    console.log("User registered successfully:", user);
    return user;
  } catch (error) {
    console.error("Registration error in auth.js:", error);
    throw error;
  }
};

export const login = async (email, password) => {
  console.log("Login function called in auth.js with email:", email);
  try {
    console.log("Attempting to sign in with Firebase...");
    const userCredential = await signInWithEmailAndPassword(
      auth,
      email,
      password
    );
    console.log("Sign in successful, user:", userCredential.user);
    return userCredential.user;
  } catch (error) {
    console.error("Login error in auth.js:", error);
    throw error;
  }
};

export const logout = async () => {
  try {
    await signOut(auth);
  } catch (error) {
    console.error("Logout error:", error);
    throw error;
  }
};
