import React from "react";
import "./About.css";
// Comment out or remove the next line if you don't have an author image
// import authorImage from '../../images/author.jpg';

function About() {
  return (
    <section className="about">
      {/* If you don't have an author image, you can comment out or remove the next line */}
      {/* <img src={authorImage} alt="Author" className="about__image" /> */}
      <div className="about__content">
        <h2 className="about__title">About the author</h2>
        <p className="about__text">
          This block describes the project author. Here you should indicate your
          name, what you do, and which development technologies you know.
        </p>
        <p className="about__text">
          You can also talk about your experience with Practicum, what you
          learned there, and how you can help potential customers.
        </p>
      </div>
    </section>
  );
}

export default About;
